<?php/* * To change this template, choose Tools | Templates * and open the template in the editor. */class WItemHistory extends CWidget {    public function init() {        parent::init();    }        public function getItems(){        if (isset(Yii::app()->request->cookies['history'])) {            $ids = Yii::app()->request->cookies['history']->value;            $item_ids = explode(',', $ids);            foreach($item_ids as $item_id){                $items[] = Item::model()->findByPk($item_id);            }            return $items;        }    }    public function run() {        $this->render('itemHistory');    }}